func TestOutput (t *testing.T) {
    file, err := os.Open("./measurements.txt")

    if err != nil {
        log.Fatal(err)
    }

    defer file.Close()

	str := solve(file)

	if str != output {
		t.Errorf("Expected output is not met, got: %s", str)
	}
}
